mapper.select:
select ${selectField}
from ${tableName} as tb
where is_deleted = 0
  and id = #{id}

select ${selectField}
from ${tableName} as tb
where is_deleted = 0
  and id = #{id}

select ${selectField}
from ${tableName} as tb
where is_deleted = 0
  and ${whereFieldName} = #{whereFieldValue}
order by (id + 0) desc

select ${selectField}
from ${tableName} as tb
where is_deleted = 0
  and ${whereFieldName} = #{whereFieldValue}
order by ${orderByField} ${orderStr}

select ${selectField} from ${tableName} as tb where is_deleted=0 and ${whereFieldName} in
#{item}
order by (id+0) desc

select IFNULL(count(1), 0)
from ${tableName}
where batch_code = #{batchCode}
  and is_deleted = 0
  and handle_flag = 1

select IFNULL(total_num, 0)
from ${tableName}
where batch_code = #{batchCode}
  and is_deleted = 0 limit 1

select *
from blade_user_oauth
where uuid = #{uuid}
  and source = #{source}
