mapper.select:
SELECT lm.id as id, wx.wx_name, lm.message_content as message_q, lma.message_content as message_a, lm.message_time, lma.message_time as answer_time FROM chat_log_message lm INNER JOIN chat_wxuser wx on lm.wxuser_id =wx.id LEFT JOIN chat_log_message lma on lma.pid=lm.id and lma.message_type ='a' WHERE lm.is_deleted =0 and wx.is_deleted =0 and lm.message_type ='q'
and lm.id =#{params.id}
and wx.wx_name like concat('%', #{params.wx_name}, '%')
and lm.blade_user_id =#{params.blade_user_id}
and lm.view_type =#{params.view_type}
and SUBSTRING_INDEX(#{params.message_time}, ',', 1) <= DATE_FORMAT(lm.message_time, '%Y-%m-%d') and SUBSTRING_INDEX(#{params.message_time}, ',', -1) >= DATE_FORMAT(lm.message_time, '%Y-%m-%d')
order by lm.id+0 desc

SELECT * FROM ( SELECT wuser.id as id, IFNULL(wuser.wx_name,'匿名用户') AS wx_name, wuser.wx_avatar, wuser.last_login_time, wuser.register_time, wuser.phone, wuser.expire_time, wuser.stop_send_time, (select ifnull(count(id),0) from chat_log_message where is_deleted =0 and wxuser_id=wuser.id and message_type ='q' ) as view_cou, wuser.question_cou, wuser.invite_code, (select ifnull(count(id),'0') from chat_wxuser where is_deleted =0 and pid=wuser.id ) as view_sub_member, (select ifnull(count(id),'0') from chat_log_share where is_deleted =0 and wxuser_id=wuser.id ) as view_share_cou, p_wuser.wx_name as view_p_wx_name, ifnull(wal.amount,0) as view_amount, wuser.is_agent, wuser.source, wuser.rl_cou, wuser.rl_used_cou, wuser.last_login_ip FROM chat_wxuser wuser left join chat_wxuser p_wuser on wuser.pid =p_wuser.id left join chat_wxuser_wallet wal on wal.wxuser_id =wuser.id WHERE wuser.is_deleted =0
and wuser.wx_name like concat('%', #{params.wx_name},'%')
and p_wuser.wx_name = #{params.view_p_wx_name}
and wuser.phone like concat('%', #{params.phone},'%')
and wuser.question_cou <= #{params.question_cou}
and SUBSTRING_INDEX(#{params.register_time}, ',', 1) <= DATE_FORMAT(wuser.register_time, '%Y-%m-%d') and SUBSTRING_INDEX(#{params.register_time}, ',', -1) >= DATE_FORMAT(wuser.register_time, '%Y-%m-%d')
) t where 1=1
order by id+0 ${params.order}
order by view_cou+0 ${params.order}
order by question_cou+0 ${params.order}
order by view_sub_member+0 ${params.order}
order by view_share_cou+0 ${params.order}

SELECT wx.wx_name as view_wx_name, lm.message_content as view_question_title, ln.* FROM chat_log_num ln left join chat_wxuser wx on ln.wxuser_id =wx.id left join chat_log_message lm on ln.question_id = lm.id WHERE ln.is_deleted =0
and wx.wx_name like concat('%', #{params.view_wx_name}, '%')
and ln.service_type = #{params.service_type}
and ln.type = #{params.type}
order by ln.id+0 desc

SELECT
    *
from (
    SELECT
        wxuser.wx_name,
        (select ifnull(count(id),0) from chat_wxuser where pid=wxuser.id and is_deleted =0 and  #{params.view_time}=DATE_FORMAT(create_time, '%Y-%m-%d')) as yq_cou,
        (select ifnull(count(id),0) from chat_log_share where wxuser_id=wxuser.id and is_deleted =0 and  #{params.view_time}=DATE_FORMAT(create_time, '%Y-%m-%d')) as fx_cou,
        (select ifnull(count(id),0) from chat_log_message where wxuser_id=wxuser.id and is_deleted =0 and  #{params.view_time}=DATE_FORMAT(create_time, '%Y-%m-%d')) as sy_cou
    FROM
        chat_wxuser wxuser
    where is_deleted=0
) t where 1=1

    and wx_name like concat('%', #{params.wx_name}, '%')


     and yq_cou >0  order by yq_cou ${params.order} 


     and fx_cou >0  order by fx_cou ${params.order} 


     and sy_cou >0  order by sy_cou ${params.order}

SELECT wh.id as id, wx.wx_name, wx.phone, wh.type as wallet_type, wh.service_type, wh.before_amount, wh.amount, wh.after_amount, wh.remark, wh.create_time as wallet_time FROM chat_wxuser_wallet_history wh INNER JOIN chat_wxuser wx ON wh.wxuser_id = wx.id WHERE wh.is_deleted = 0 AND wx.is_deleted =0
and wx.wx_name like concat('%', #{params.wx_name}, '%')
and wx.phone like concat('%', #{params.phone}, '%')
and wh.wallet_type = #{params.wallet_type}
and wh.service_type = #{params.service_type}
order by wh.id+0 desc
